YUI.add("gallery-charts-legend",function(a){a.Chart.Legend=a.Base.create("Legend",a.Plugin.Base,[],{initializer:function(b){this.afterHostEvent("render",this.renderLegend);},renderLegend:function(){var b=this.get("container"),d=b.setContent("<h3>Legend</h3><ul class='yui3-charts-legend'></ul>").one("ul"),c=this.get("host").get("seriesCollection");a.Array.each(c,function(e){var f={label:e.get("yKey"),color:e.get("styles").marker.fill.color};d.append(a.Lang.sub("<li style='border-left: 1em solid {color};'>{label}</li>",f));});}},{NS:"Legend",ATTRS:{"container":{setter:function(b){if(a.Lang.isString(b)){return a.one(b);}return new a.Node(b);}}}});},"@VERSION@",{requires:["node","charts","plugin"]});